mapper.update:
UPDATE sys_email_account
SET del_flag = '1'
WHERE username = #{emailAddress}

mapper.select:
SELECT se.id, se.username, se.password, se.domain
FROM sys_user_email sue,
     sys_email_account se
WHERE sue.email_id = se.id
  AND sue.user_id = #{userId}
  AND se.username = #{emailAddress}
